mapper.update:

	update rboard
	set hit = hit+1
	where no = #{no}


	update rboard
	set order_no = order_no+1
	where group_no = #{groupNo}


	update rboard
	set depthcount = depthcount+1
	where group_no = #{groupNo}


	update rboard
	set content =#{content},
		title = #{title}
	where no = #{no}
